import React, { useEffect, useState } from 'react';

const storageKey = 'theme-preference';

const ThemeToggle = () => {
    const [theme, setTheme] = useState(getColorPreference());

    function onClick() {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        setTheme(newTheme);
        newTheme === 'dark' ? toMoon() : toSun();
        setPreference(newTheme);
    }

    function toMoon() {
        document.querySelector('.moon').classList.add('disp');
        document.querySelector('.sun').classList.remove('disp');
    }

    function toSun() {
        document.querySelector('.moon').classList.remove('disp');
        document.querySelector('.sun').classList.add('disp');
    }

    function getColorPreference() {
        if (localStorage.getItem(storageKey)) {
            return localStorage.getItem(storageKey);
        } else {
            return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
        }
    }

    function setPreference(selectedTheme) {
        localStorage.setItem(storageKey, selectedTheme);
        reflectPreference(selectedTheme);
    }

    function reflectPreference(selectedTheme) {
        selectedTheme === 'dark' ? toMoon() : toSun();
        document.documentElement.setAttribute('data-theme', selectedTheme);
        document.querySelector('#theme-toggle')?.setAttribute('aria-label', selectedTheme);
    }

    useEffect(() => {
        reflectPreference(theme);
    }, [theme]);

    useEffect(() => {
        function handleColorSchemeChange(event) {
            const isDark = event.matches;
            const newTheme = isDark ? 'dark' : 'light';
            setTheme(newTheme);
            setPreference(newTheme);
        }

        const colorSchemeQuery = window.matchMedia('(prefers-color-scheme: dark)');
        colorSchemeQuery.addEventListener('change', handleColorSchemeChange);

        return () => {
            colorSchemeQuery.removeEventListener('change', handleColorSchemeChange);
        };
    }, []);

    return (
        <button id="theme-toggle" title="Toggle light / dark theme" onClick={onClick}>
            <div className="moon"><ion-icon name="moon"></ion-icon></div>
            <div className="sun"><ion-icon name="sunny"></ion-icon></div>
        </button>
    );
};

export default ThemeToggle;
