import React, { useState } from 'react';

const Result = () => {
    return (
        <p className="success-message">Your message has been successfully sent. I will contact you soon.</p>
    );
};

function ContactForm() {
    const [registrationStatus, setRegistrationStatus] = useState('');
    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [loading, setLoading] = useState(false);

    async function register(ev) {
        ev.preventDefault();
        setLoading(true);
        try {
            const response = await fetch('http://localhost:4000/register', {
                method: 'POST',
                body: JSON.stringify({ username, name }),
                headers: { 'Content-Type': 'application/json' },
            });
            if (response.status === 200) {
                setRegistrationStatus('success');
                setUsername('');
                setName('');
            } else {
                setRegistrationStatus('failed');
            }
        } catch (error) {
            console.error('Error registering:', error);
            setRegistrationStatus('error');
        } finally {
            setLoading(false);
        }
    }

    return (
        <form action="" onSubmit={register}>
            <div className="rn-form-group">
                <input
                    type="text"
                    name="username"
                    value={username}
                    onChange={ev => setUsername(ev.target.value)}
                    placeholder="Enter your username"
                    required
                />
            </div>
            <div className="rn-form-group">
                <input
                    type="text"
                    name="name"
                    value={name}
                    onChange={ev => setName(ev.target.value)}
                    placeholder="Enter your name"
                    required
                />
            </div>
            <div className="rn-form-group">
                <button className="rn-button-style--2 btn-solid" type="submit" value="submit" name="submit" id="mc-embedded-subscribe" disabled={loading}>
                    {loading ? 'Submitting...' : 'Submit Now'}
                </button>
            </div>

            <div className="rn-form-group">
                {registrationStatus === 'success' ? <Result /> : null}
                {registrationStatus === 'failed' ? <p className="error-message">Registration failed. Please try again later.</p> : null}
            </div>
        </form>
    );
}

export default ContactForm;
