import React, { useState, useEffect } from 'react';
import './ThemeToggler.css';

const ThemeToggler = () => {
  const storageKey = 'theme-preference';
  const [theme, setTheme] = useState(getColorPreference());

  function getColorPreference() {
    if (localStorage.getItem(storageKey)) {
      return localStorage.getItem(storageKey);
    } else {
      return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
    }
  }

  const setPreference = () => {
    localStorage.setItem(storageKey, theme);
    reflectPreference();
  };

  const reflectPreference = () => {
    theme === 'dark' ? toMoon() : toSun();
    document.documentElement.setAttribute('data-theme', theme);
    document.querySelector('#theme-toggle')?.setAttribute('aria-label', theme);
  };

  const toMoon = () => {
    document.querySelector('.moon').classList.add('disp');
    document.querySelector('.sun').classList.remove('disp');
  };

  const toSun = () => {
    document.querySelector('.moon').classList.remove('disp');
    document.querySelector('.sun').classList.add('disp');
  };

  const onClick = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    newTheme === 'dark' ? toMoon() : toSun();
    setPreference();
  };

  useEffect(() => {
    reflectPreference();
  }, []);

  useEffect(() => {
    window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', ({ matches: isDark }) => {
      setTheme(isDark ? 'dark' : 'light');
      setPreference();
    });

    return () => {
      window.matchMedia('(prefers-color-scheme: dark)').removeEventListener('change', ({ matches: isDark }) => {
        setTheme(isDark ? 'dark' : 'light');
        setPreference();
      });
    };
  }, []);

  return (
    <button id="theme-toggle" title="Toggle light / dark theme" onClick={onClick}>
      <div className="moon">
        <ion-icon name="moon"></ion-icon>
      </div>
      <div className="sun">
        <ion-icon name="sunny"></ion-icon>
      </div>
    </button>
  );
};

export default ThemeToggler;
